resources.string:
        .CODE ;
        .STARTUP ;
        AddPackedDecimal proc           ;
        push    ax                      ;
        push    bx                      ;
        push    cx                      ;
        push    dx                      ;
        push    di                      ;
        push    si                      ;
        mov     al,[si]                 ;
        mov     ah,[bx]                 ;
        mov     dx,ax                   ;
        and     dx,00f0fh               ;
        and     ax,0f0f0h               ;
        cmp     dl,dh                   ;
        stc                             ;
        jnz     Done                    ;
        std                             ;
        clc                             ;
        push    di                      ;
AddEmUp:                                ;
        adc     al,ah                   ;
        daa                             ;
        stosb                           ;
        lahf                            ;
        dec     si                      ;
        dec     bx                      ;
        sahf                            ;
        mov     al,[si]                 ;
        mov     ah,[bx]                 ;
        loop    AddEmUp                 ;
        pop     di                      ;
        jc      Done                    ;
        or      [di],dl                 ;
Done:                                   ;
        pop     si                      ;
        pop     di                      ;
        pop     dx                      ;
        pop     cx                      ;
        pop     bx                      ;
        pop     ax                      ;
        ret                             ;
AddPackedDecimal endp                   ;
        END
